# -*- coding: utf-8 -*-
require 'isono'

module Dcmgr
  module NodeModules
    class ResourceCollector < Isono::NodeModules::Base
      include Isono::NodeModules
      include Dcmgr::Logger

      initialize_hook do
        rpc = RpcChannel.new(node)
        app = Isono::Rack::ObjectMethod.new(myinstance)
        rpc.register_endpoint('resource-collector', Isono::Rack.build do
            use Isono::Rack::DataStore
            run proc { |req, res|
              Thread.current[Models::BaseNew::LOCK_TABLES_KEY] = {}
              app.call(req, res)
            }
          end)
      end

      terminate_hook do
      end

      def resource_store(resource_id, data)
        logger.info("Store Resource: #{resource_id}: #{data}")
        # do not respond model object.
        nil
      end
    end
  end
end
